//时间
function pad(content, count, padStr) {
	count = count || 2
	padStr = padStr || '0'
	content = String(content)
	return content.padStart(count, padStr)
}
let da = ''
setInterval(() => {
	let date = new Date()
	let month = pad(date.getMonth() + 1)
	let day = pad(date.getDate())
	da = month.concat(day)
}, 1000);



